import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SearchBar.css';

function SearchBar() {
  const [input, setInput] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);
  const navigate = useNavigate();

  // Local Storage에서 검색 기록 불러오기
  useEffect(() => {
    const savedHistory = JSON.parse(localStorage.getItem('searchHistory')) || [];
    setSearchHistory(savedHistory);
  }, []);

  // 검색 기록 저장 함수
  const saveSearchHistory = (query) => {
    const updatedHistory = [query, ...searchHistory.filter((item) => item !== query)].slice(0, 5); // 중복 제거 및 최대 5개 유지
    setSearchHistory(updatedHistory);
    localStorage.setItem('searchHistory', JSON.stringify(updatedHistory));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (input.trim() === '') return;

    try {
      // 백엔드의 검색 API 호출
      const response = await axios.get('/api/places/search', {
        params: { query: input },
      });
      const place = response.data.results[0];

      // 검색 기록 저장
      saveSearchHistory(input);

      // 검색 결과 페이지로 이동
      navigate('/results', { state: { query: input, place } });
    } catch (error) {
      console.error('검색 에러:', error);
      // 에러 처리 로직 추가 (예: 사용자에게 에러 메시지 표시)
    }
  };

  const handleHistoryClick = (query) => {
    setInput(query); // 클릭한 검색 기록을 검색창에 설정
  };

  return (
      <div className="search-bar-container">
        <form className="search-bar" onSubmit={handleSubmit}>
          <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="여행지, 맛집 등 정보를 입력하세요."
              onFocus={(e) => (e.target.placeholder = '')}
              onBlur={(e) => (e.target.placeholder = '여행지, 맛집 등 정보를 입력하세요.')}
          />
          <button type="submit">검색</button>
        </form>
        {searchHistory.length > 0 && (
            <ul className="search-history">
              {searchHistory.map((query, index) => (
                  <li key={index} onClick={() => handleHistoryClick(query)}>
                    {query}
                  </li>
              ))}
            </ul>
        )}
      </div>
  );
}

export default SearchBar;
