import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import ChatWindow from "../components/ChatWindow/ChatWindow";
import axios from "axios";
import "./ResultPage.css";

function ResultPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { place } = location.state;
  const [analysis, setAnalysis] = useState(null);
  const [details, setDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [tooltip, setTooltip] = useState({ visible: false, text: "", x: 0, y: 0 });
  const [loadingDots, setLoadingDots] = useState("...");

  // Î°úÎî© ÌôîÎ©¥ Ï†ê Ïï†ÎãàÎ©îÏù¥ÏÖò
  useEffect(() => {
    const interval = setInterval(() => {
      setLoadingDots((prevDots) => (prevDots.length === 3 ? "." : prevDots + "." ));
    }, 500);

    return () => clearInterval(interval);
  }, []);

  // Google ÏßÄÎèÑ ÎßÅÌÅ¨ ÏÉùÏÑ±
  const generateGoogleMapsLink = (placeName, address) => {
    const query = encodeURIComponent(`${placeName} ${address}`);
    return `https://www.google.com/maps/search/?api=1&query=${query}`;
  };

  // Í∏çÏ†ï Î∞è Î∂ÄÏ†ï ÎπÑÏú® Í≥ÑÏÇ∞
  const calculateSentimentPercentage = (sentiments) => {
    const total = sentiments.positive + sentiments.negative;
    if (total === 0) return { positive: 50, negative: 50 };
    return {
      positive: (sentiments.positive / total) * 100,
      negative: (sentiments.negative / total) * 100,
    };
  };

  const handleMouseEnter = (e, sentiments) => {
    const { positive, negative } = calculateSentimentPercentage(sentiments);
    const { clientX, clientY } = e;
    setTooltip({
      visible: true,
      text: `Í∏çÏ†ï: ${positive.toFixed(1)}% | Î∂ÄÏ†ï: ${negative.toFixed(1)}%`,
      x: clientX + 10,
      y: clientY + 10,
    });
  };

  const handleMouseLeave = () => {
    setTooltip({ visible: false, text: "", x: 0, y: 0 });
  };

  useEffect(() => {
    const fetchDetailsAndAnalysis = async () => {
      try {
        // Ïû•ÏÜå ÏÉÅÏÑ∏ Ï†ïÎ≥¥ ÏöîÏ≤≠
        const detailsResponse = await axios.get("/api/places/details", {
          params: { place_id: place.place_id },
        });

        const details = detailsResponse.data;
        setDetails(details);

        const reviews = details.reviews?.map((review) => review.text) || [];

        if (reviews.length === 0) {
          setAnalysis({
            summary: ["ÌïúÍµ≠Ïñ¥ Î¶¨Î∑∞Í∞Ä ÏóÜÏäµÎãàÎã§."],
            advantages: [],
            disadvantages: [],
            usefulInfo: [],
            // petFriendly, recommendedActivities, foodAndDrink, priceRange, eventDetails, culturalPoints Îì±Ïù¥ ÌïÑÏöîÌïòÎã§Î©¥ Ïó¨Í∏∞ÎèÑ Í∏∞Î≥∏Í∞í ÏÑ§Ï†ï
            sentiments: { positive: 0, negative: 0 },
          });
          setLoading(false);
          return;
        }

        // Î¶¨Î∑∞ Î∂ÑÏÑù ÏöîÏ≤≠
        const analysisResponse = await axios.post("/api/openai/analyze", {
          reviews,
        });

        // analysisResponse.dataÏóê petFriendly, recommendedActivities, foodAndDrink, priceRange, eventDetails, culturalPoints Îì± ÌïÑÎìúÎ•º Ï∂îÍ∞ÄÌñàÎã§Î©¥ Ïó¨Í∏∞ÏÑú Íµ¨Ï°∞Î∂ÑÌï¥ Ìï†Îãπ
        const {
          summary,
          advantages,
          disadvantages,
          usefulInfo,
          sentiments,
